package operator;

public class basic3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 200;
		int y = 100;
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
		
		
	}

}
