package 복습;

public class 기본형데이터 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//정수, 실수, 문자, 논리
		//대입연산자
		int count = 20;
		double 비율 = 0.3;
		char floor = '7';
		boolean hot = true;
		//결합연산자
		System.out.println("총 인원은 "+count+"명 입니다.");
		System.out.println("남녀 비율은 "+비율+"입니다.");//갈색 비율은 변수이다.
		System.out.println("층수는"+floor+"층 입니다.");
		System.out.println("더운가요" + hot);

	}

}
